	nom VARCHAR(30),
	adresse VARCHAR(30),
	prenom VARCHAR(30),
	mdp VARCHAR(15),
	dateDeNaissance DATE                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
);